using System.Collections;using System.Collections.Generic;using System.Collections.Specialized;using DragonU3DSDK;using UnityEngine;public sealed class DeepLinkPlugin{    private static DeepLinkPlugin instance = null;    private static readonly object syslock = new object();    public static DeepLinkPlugin Instance    {        get        {            if (instance == null)            {                lock (syslock)                {                    if (instance == null)                    {                        instance = new DeepLinkPlugin();                    }                }            }            return instance;        }    }    private DeepLinkPlugin()    {    }    public void Init()    {#if !UNITY_STANDALONE        ImaginationOverflow.UniversalDeepLinking.DeepLinkManager.Instance.LinkActivated += Instance_LinkActivated;#endif        // DebugUtil.Log("DeepLink New Init Finished");    }    //此后实现其他具体逻辑    private void Instance_LinkActivated(ImaginationOverflow.UniversalDeepLinking.LinkActivation s)    {        DebugUtil.Log("DeepLink New RawQueryString: " + s.RawQueryString);        DebugUtil.Log("DeepLink New QueryString: " + s.QueryString);        DebugUtil.Log("DeepLink New Uri: " + s.Uri);        if (!string.IsNullOrEmpty(s.RawQueryString))        {            NameValueCollection nvc = DragonU3DSDK.Utils.ParseQueryString(s.RawQueryString);            DragonU3DSDK.EventManager.Instance.Trigger<DragonU3DSDK.SDKEvents.DeepLinkEvent>().Data(nvc).Trigger();            PlayerPrefs.SetString("DeepLinkRawQueryString", s.RawQueryString);            DragonU3DSDK.Network.BI.BIManager.Instance.SendCommonGameEvent(DragonU3DSDK.Network.API.Protocol.BiEventCommon.Types.CommonGameEventType.DeepLink, s.Uri, s.RawQueryString);        }    }}